<?php
namespace Tests\Feature;

use App\User;
use Tests\TestCase;
use Illuminate\Foundation\Testing\RefreshDatabase;

class NotesTest extends TestCase
{
  use RefreshDatabase;

  public function setUp()
  {
    parent::setUp();
    $user = factory('App\User')->create();
    $this->be($user);
    $this->book = factory('App\Book')->create(['user_id' => $user->id]);
    $this->note = factory('App\Note')->create(['user_id' => $user->id, 'book_id' => $this->book->id]);
  }
  
  /** @test */
  public function a_note_can_be_updated()
  {
    $newText = 'Here is an updated note';

    $this->updateNote(['note' => $newText]);
    
    $this->assertDatabaseHas('notes', [
          'id' => $this->note->id,
          'note' => $newText
        ]);
  }

  /** @test */
  public function a_user_cannot_update_another_users_note()
  {
    $newText = 'Here is an updated note';

    $newUser = factory('App\User')->create();
    
    $this->be($newUser);

    $this->updateNote(['note' => $newText]);

    $this->assertDatabaseMissing('notes', [
      'id' => $this->note->id,
      'note' => $newText
    ]);
  }

  /** @test */
  public function an_updated_note_cannot_be_empty()
  {
    $newText = '';

    $this->updateNote(['note' => $newText])
        ->assertSessionHasErrors('note');
  }

  /** @test */
  public function an_updated_note_cannot_be_longer_than_4000_characters()
  {
    $newText = '';

    $this->updateNote(['note' => $newText])
        ->assertSessionHasErrors('note');
  }

  protected function updateNote($attributes)
  {
    return $this->post('/notes/' . $this->note->id . '/update', $attributes);
  }

  /** @test */
  public function a_note_can_be_soft_deleted()
  {
    $this->delete('/notes/' . $this->note->id);
    
    $this->assertSoftDeleted('notes', [
      'id' => $this->note->id,
      'note' => $this->note->note
    ]);
  }
  
  /** @test */
  public function a_user_cannot_soft_delete_another_users_note()
  {
    $newUser = factory('App\User')->create();
    
    $this->be($newUser);

    $this->delete('/notes/' . $this->note->id);

    $this->assertDatabaseHas('notes', [
      'id' => $this->note->id,
      'note' => $this->note->note,
      'deleted_at' => null
    ]);
  }

  /** @test */
  public function notes_are_visible_on_the_books_page()
  {
    $this->get('/books/' . $this->book->id . '/notes')
      ->assertSee($this->note->note);
  }

  /** @test */
  public function soft_deleted_notes_are_not_visible_on_the_books_page()
  {
    $this->delete('/notes/' . $this->note->id);

    $this->get('/books/' . $this->book->id . '/notes')
      ->assertDontSee($this->note->note);
  }
}
